import { useEffect, useState } from "react";
import axios from "axios";
import React from 'react';
import { Link } from "react-router-dom";

const Payment =()=>{
    const totalPayment =localStorage.getItem('totalPayment');
    const paymentUrl = `http://localhost:5555/user/na/pay`;
    const [payment, setPayment] = useState({});

    useEffect(() => {
        setPayment({
            "userName":localStorage.getItem("name"),
            "totalPayment":totalPayment
        });

    }, [])
    const handleAddAppUser = (evt) => {
        evt.preventDefault();
        setPayment(
            {
                ...payment,
                [evt.target.name]: evt.target.value
            });
    }
    const savePayment = () => {

        console.log(payment)
        axios.post(paymentUrl, payment)
            .then((response) => {
                console.log(response.data);
                alert(`payment done successfully`);
            })
            .catch(() => {
                alert("cannot process request at the moment");
            });
    }

    return(<center>
        <div class="container" >
            <div className="col-6 border border-light shadow p-3 mb-5 bg-white" style={{textAlign:"center"}}>
            <h5 className="text-dark">Hi, {localStorage.getItem("name")}</h5>
            <h4><b>Enter Payment Details</b></h4>
            <input className="form-control mt-3" type="text" id="cardNumber" name="cardNumber" value={payment.cardNumber} onChange={handleAddAppUser} placeholder="Valid Card Number" />

    <div class="row">
        <div class="col-xs-12 col-md-4 border border-light shadow p-3 mb-5 bg-white">
            <div class="panel panel-default">
                                <div class="panel-body">
                    <form role="form">
                    <div class="form-group">
                        <label for="cardNumber">
                            CARD NUMBER</label><br/>
                        <div class="input-group">
                            <input type="text" class="form-control" id="cardNumber" value={payment.cardNumber} name="cardNumber" onChange={handleAddAppUser} placeholder="Valid Card Number"
                                required autofocus />
                            <span class="input-group-addon"><span class="glyphicon glyphicon-lock"></span></span>
                        </div>
                    </div>
                    <div class="row float-container">
                        <div class="col-xs-7 col-md-7 float-container">
                            <div class="form-group ">
                                <label for="expityMonth">
                                    EXPIRY DATE</label>
                                <div class="float-child col-xs-5 col-lg-5 pl-ziro " style={{'padding-left':'0px','float':'left'}}>
                                    <input type="text" class="form-control" name="expMonth" value={payment.expMonth}  id="expityMonth" placeholder="MM"onChange={handleAddAppUser} required />
                                </div>
                                <div class="float-child col-xs-5 col-lg-5 pl-ziro pull-right"style={{'padding-left':'0px','float':'left'}}>
                                    <input type="text" class="form-control" name="expYear" value={payment.expYear}id="expityYear" placeholder="YY"onChange={handleAddAppUser} required /></div>
                            </div>
                        </div>
                        <div class="float-child col-xs-5 col-md-5 pull-right">
                            <div class="form-group">
                                <label for="cvCode">
                                    CV CODE</label>
                                <input type="password" class="form-control" name="cvv" value={payment.cvv} id="cvCode" placeholder="CV" onChange={handleAddAppUser}required />
                            </div>
                        </div>
                    </div>
                    </form>
                </div>
            </div>
            <div style={{'background':'#7FB3D5'}}>
                <p style={{'margin':'15px'}}>Total Payment :<i class="fa fa-inr" aria-hidden="true"></i>{totalPayment}/-</p>
                </div>

           <Link to="/search"> <button class="btn btn-success btn-lg btn-block" type="submit" onClick={()=>{if (window.confirm('Are you sure you want to proceed?')){savePayment()}}}>Pay</button></Link>
        </div>
    </div>
</div>
</div>
</center> );
}
export default Payment
