import axios from "axios";
import { useEffect, useState } from "react";

const GetPlayers = () => {

    const [userId, setUserId] = useState();
    const [appUser, setAppUser] = useState({});
    const [appUsersList, setAppUsersList] = useState([]);

    const dataUrl = `http://localhost:4521/players`;

    useEffect(() => {
        console.log(`useEffect`);

    }, []);

    const handleChange = (evt) => {
        evt.preventDefault();
        console.log(evt.target.value);
        setUserId(evt.target.value);
    }

    const submitGetAppUserById = (evt) => {
        evt.preventDefault();
        axios.get(`${dataUrl}/${userId}`)
            .then((response) => {
                console.log(response.data);
                setAppUser(response.data);
            })
            .catch(() => {
                alert("User not found.");
                setUserId();
            });
    }

    const submitGetAllAppUsers = (evt) => {
        evt.preventDefault();
        axios.get(`${dataUrl}`)
            .then((response) => {
                console.log(response.data);
                setAppUsersList(response.data);
            })
            .catch(() => {
                alert("Users not found.");
                setAppUsersList([]);
            });
    }
    return (
        <div className="container" >
            <p className="display-4 text-dark">Get Players</p>


            <div>
                <div className="col-8 border border-light shadow p-3 mb-5 bg-white">
                    <p>Find Player by id</p>
                    <form className="form form-group form-primary" onSubmit={submitGetAppUserById}>
                        <input className="form-control mt-3" type="number" id="userId" name="userId" value={userId} onChange={handleChange} placeholder="Enter Id to search" />
                        <input className="form-control mt-3 btn btn-info" type="submit" value="Find appUser" />
                    </form>
                    {<p className="text-dark"> <span className="font-weight-bold"> Player Data: </span><br />  {appUser.playerId} <br />{appUser.playerName} <br />{appUser.team} <br /></p>}

                </div>
            </div>


            <div>
                <div className="col-8 border border-light shadow p-3 mb-5 bg-white">
                    <p>Find All Players</p>
                    <form className="form form-group form-info" onSubmit={submitGetAllAppUsers}>
                        <input className="form-control mt-3 btn btn-info" type="submit" value="Find All Players" />
                    </form>

                    <div><span className="font-weight-bold"> All Players Data: </span>
                        {<div><table class="table table-striped">
                            <tbody>
                                <tr>
                                <th scope="col">S.no</th>
                                    <th scope="col">NAME</th>
                                    <th scope="col">ID</th>
                                    <th scope="col">TEAM</th>
                                </tr>
                                {appUsersList.map((item, i) => (
                                    <tr key={i}>
                                        <th scope="row">{i+1}</th>
                                        <td>{item.playerName}</td>
                                        <td>{item.playerId}</td>
                                        <td>{item.team}</td>
                                    </tr>
                ))}
            </tbody>
                       </table> </div>}
                    </div>
                </div>
            </div>
        </div>
    );

}

export default GetPlayers;

