import { useState } from "react";
import './style.css';
import React from "react";
import axios from "axios";

function UserHome(){

    const [bus,setBus]=useState();
    const [dataUrl,setDataUrl]=useState(`http://localhost:1212/bus/route`);
    const [routeFrom,setRouteFrom]=useState();
    const [routeTo,setRouteTo]=useState();
    const [item,setItem]=useState({"busId":233,
    "routeFrom":"Hyderabad",
    "routeTo":"Mumbai",
    "from":"22-22-22",
    "till":"22-2-22",
    "type":"non-ac",
    "fare":2000
});
const [busList,setBusList]=useState([]);

const cartUrl=`http://localhost:1112/cart`;

    const handleInputChange1=(e)=>{
        e.preventDefault();
        setBus({
            ...bus,
            "routeFrom":e.target.value
        });
        setRouteFrom(e.target.value);

    }
    const handleInputChange2=(e)=>{
        e.preventDefault();
        setBus({
            ...bus,
            "routeTo":e.target.value
        });
        setRouteTo(e.target.value);

    }

    const onSearch=(e)=>{
        e.preventDefault();
        localStorage.setItem("routeFrom",routeFrom);
        localStorage.setItem("routeTo",routeTo);
        console.log(localStorage.getItem("routeFrom"));
        console.log(localStorage.getItem("routeTo"));
        axios.get(`${dataUrl}/${localStorage.getItem("routeFrom")}/${localStorage.getItem("routeTo")}`)
            .then((response)=>{
                console.log(response.data.articles);
                setBusList(response.data);
            })
            .catch(()=>{
                alert("Data not found.");
                setBusList([]);
            });
    }

    const saveData=(article)=>{
        article.userName=localStorage.getItem("name");
        axios.post(cartUrl,article)
        .then((response)=>{
            alert('Added to Cart');

        })
        .catch((err)=>{
            alert('Sorry could not be added');
            console.log(err.response);
        });
    }



    return(
        <div className="container">
            <marquee width="90%" direction="left" height="10px">Gone are the days when people used to plan for hours to get their bus reservation. Be it long queues in front of ticket booking offices or prolonged waiting time over the telephone, people had to go through a lot of hassles while booking bus tickets. But now the scenario has completely changed in the Indian bus travel industry. With the increasing popularity and adoption of on-demand bus booking apps, now the entire process of making a bus travel reservation has become quick and hassle-free.</marquee>
            <marquee behavior="scroll" direction="left" scrollamount="20" bgcolor="">DISCOUNTS AVAILABLE NOW!</marquee>

            <div>

                <div className="form">
                    <div className="form-body">
                    <h4 style={{textAlign:"center"}}>Hi, {localStorage.getItem("name")} </h4>
                    <h3 style={{textAlign:"center",color:"teal"}}>
                    Hope you have a great journey!
                </h3>
                <p style={{textAlign:"center"}}>Search for bus </p>
                    <div className="routeFrom">
                    <label className="form__label" for="lastName">From</label>
                    <select name="routeFrom" >
                        <option name="routeFrom" value="Goa" onClick={(e) => handleInputChange1(e)}>Goa</option>
                        <option name="routeFrom" value="Hyderabad" onClick={(e) => handleInputChange1(e)}>Hyderabad</option>
                        <option name="routeFrom" value="Bangalore" onClick={(e) => handleInputChange1(e)}>Bangalore</option>
                        <option name="routeFrom" value="Mumbai" onClick={(e) => handleInputChange1(e)}>Mumbai</option>
                    </select>
                </div>
                <div className="routeTo">
                    <label className="form__label" for="lastName">To</label>
                    <select name="routeTo" >
                        <option name="routeTo" value="Goa" onClick={(e) => handleInputChange2(e)}>Goa</option>
                        <option name="routeTo" value="Hyderabad" onClick={(e) => handleInputChange2(e)}>Hyderabad</option>
                        <option name="routeTo" value="Bangalore" onClick={(e) => handleInputChange2(e)}>Bangalore</option>
                        <option name="routeTo" value="Mumbai" onClick={(e) => handleInputChange2(e)}>Mumbai</option>
                    </select>
                </div>
                <input className="form-control mt-3 btn btn-success" type="submit" value="Search" onClick={(e)=>onSearch(e)}/>


                </div>

                </div>
                <div>
                <div>

      </div>
      <div>
      {busList.map((item,i)=>(
      <div className="buscard">
          <div className="busid">
            {item.busId}

          </div>
          <div className="fromto">
            <div className="froma">
                <p>From:{item.routeFrom}</p>
            </div>
            <div className="froma">
                <p>starts at:{item.from.split('T')[0]}</p>
            </div>


          </div>
          <div className="fromto">
          <div className="froma">
                <p>To:{item.routeTo}</p>
            </div>
            <div className="froma">
                <p>reaches at:{item.till.split('T')[0]}</p>
            </div>

          </div>
          <div className="type">
              {item.busType}

          </div>
          <div className="fare">
              {item.fare}

          </div>
          <button type="submit" onClick={()=>{saveData(item)}}>add to cart</button>
      </div>))}
      </div>
                </div>
            </div>

        </div>
    )
}
export default UserHome;






