import axios from "axios";
import { useEffect, useState } from "react";

const AddPlayers = () => {

    const [appUserToAdd, setAppUserToAdd] = useState({});
    const dataUrl = `http://localhost:4521/players`;

    useEffect(() => {
        console.log(`useEffect`);

    }, []);
    const handleAddAppUser = (evt) => {
        evt.preventDefault();
        setAppUserToAdd(
            {
                ...appUserToAdd,
                [evt.target.name]: evt.target.value
            });
    }

    const submitAddAppUser = (evt) => {
        evt.preventDefault();
        axios.post(dataUrl, appUserToAdd)
            .then((response) => {
                alert(`Player added successfully`);
            })
            .catch(() => {
                alert("User could not be added.");
            });
    }


    return (
        <div className="container" >
            <p className="display-4 text-dark">Add Players</p>
            <div>
                <div className="col-6 border border-light shadow p-3 mb-5 bg-black">
                    <p>Add a New Player </p>
                    <form className="form form-group form-secondary" onSubmit={submitAddAppUser}>
                        <input className="form-control mt-3" type="number" id="playerId" name="playerId" value={appUserToAdd.playerId} onChange={handleAddAppUser} placeholder="Enter id" />
                        <input className="form-control mt-3" type="text" id="playerName" name="playerName" value={appUserToAdd.playerName} onChange={handleAddAppUser} placeholder="Enter name" />
                        <input className="form-control mt-3" type="text" id="team" name="team" value={appUserToAdd.team} onChange={handleAddAppUser} placeholder="Enter team" />

                        <input className="form-control mt-3 btn btn-info" type="submit" value="Add Player" />
                    </form>
                </div>
            </div>
            </div>
    );

}

export default AddPlayers;
