    //     //or remove element from the redux state
    //     if (index !== -1) {
    //       data.splice(index, 1);
    //       this.setState({data});
    //     }
    //      this.props.history.push('/');
    //      this.forceUpdate();
    //  }

    const deleteAppUser=(id)=>{
        console.log(id);
        if(window.confirm("Are you sure?")){
            fetch(URL+"/"+id,{
                method: 'DELETE',
                header:{'Accept':'application/json','Content-Type':'application/json'}

            }); 

            fetchData()
        }


    }

    return (
        <div className="container" >
            <p className="display-4 text-dark">Delete Players</p>
            <div >
                <div className="col-8 border border-light shadow p-3 mb-5 bg-white">
                    <div><span className="font-weight-bold"> All Players Data: </span>
                        {<div><table class="table table-striped">
                            <tbody>
                                <tr>
                                <th scope="col">S.no</th>
                                    <th scope="col">NAME</th>
                                    <th scope="col">ID</th>
                                    <th scope="col">TEAM</th>
                                    <th scope="col">ACTION</th>
                                </tr>
                                {data.map((item, i) => (
                                    <tr key={i}>
                                        <th scope="row">{i+1}</th>
                                        <td>{item.name}</td>
                                        <td>{item.id}</td>
                                        <td>{item.team}</td>
                                        <td><button className="btn btn-danger" onClick={()=>deleteAppUser(item.id)}>Delete</button>

        </td>
                                    </tr>
                ))}
            </tbody>
                       </table> </div>}
                    </div>
                </div>
            </div>
        </div>
    );

}

export default DeletePlayers;

