    const submitAddAppUser = (evt) => {
        evt.preventDefault();
        appUserToAdd.mypic=mypic;
        axios.post(dataUrl, appUserToAdd)
            .then((response) => {
                localStorage.setItem("secQuestion",appUserToAdd.secQues);
                localStorage.setItem("secAnswer",appUserToAdd.secAnswer);
                alert(`Player added successfully ${response.data}`);
            })
            .catch(() => {
                alert("Player could not be added.");
            });
    }
    return (<center>
        <div className="container" >
            <p className="display-4 text-dark">Type in your new Password</p>

            <div>
                <div className="col-8 border border-light shadow p-3 mb-5 bg-white">
                    <p>Enter the details below:</p>
                    <form className="form form-group form-secondary" onSubmit={submitAddAppUser}>
                        <input className="form-control mt-3" type="text" id="userName" name="userName" value={appUserToAdd.userName} onChange={handleAddAppUser} placeholder="Enter name" />
                        <input className="form-control mt-3" type="password" id="password" name="password" value={appUserToAdd.password} onChange={handleAddAppUser} placeholder="Enter password" />
                        <input className="form-control mt-3 btn btn-success" type="submit" value="Change Password" />
                    </form>
                </div>
            </div>
        </div>
        </center>    );
}
export default ResetPassword;
