// export default Header;



const Header = () => {

    const feedUrl="http://localhost:1211/feedback";
    const [show, setShow] = useState(false);
    const [feedback,setFeedBack]=useState({});

    const handleClose1 = (e) =>{
        e.preventDefault();
        console.log(feedback);
        axios.post(`${feedUrl}`,feedback)
        .then(()=>
        {
            alert("thank you for your feedback");
        })
        //.catch(alert("something's wrong"));
        setShow(false);

        setFeedBack();

    }
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const handleChange=(e)=>{

        setFeedBack({
            ...feedback,
            "feedback":e.target.value}
        )

    }




    return (
        <header className="header sticky-top bg-info">
            <nav className="navbar navbar-fixed-top navbar-expand-lg">
                <div className="container-fluid">
                <Link to="/" className="navbar-brand align-left nav-item ">
                <img src="https://cdn1.vectorstock.com/i/1000x1000/67/25/bus-icon-vector-21156725.jpg" height="56px" alt="User-Registration" />
                    </Link>
                    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarResponsive">
                        <span className="navbar-toggler-icon"></span>
                    </button>

                    <div className="collapse navbar-collapse" id="navbarResponsive">
                        <ul className="navbar-nav ml-left">
                        <li className="nav-item text-info">
                                <Link to="/search" className="nav-link text-light" ><i class="bi-search"></i></Link>
                            </li>

                            <li className="nav-item text-info">
                                <Link to="/registration" className="nav-link text-light" ><i class="bi-person-plus-fill"></i>Register</Link>
                            </li>
                            <li className="nav-item text-info">
                                <Link to="/cart" className="nav-link text-light"><i class="bi bi-cart"></i>cart</Link>
                            </li>
                            </ul>
                            <ul className="navbar-nav ml-auto">
                            <li className="nav-item" style={{fontWeight: 'bold',color: 'red',}}>
                                {/* <Link to="/viewfav" className="nav-link" >Favs</Link> */}
                                <Link type="button" class="nav-link text-light" onClick={handleShow} data-bs-toggle="modal" data-bs-target="#exampleModal">
                                    <i class="bi-chat-square-text"></i>Feedback
                                </Link>
                                <Modal show={show} onHide={handleClose} centered="true">
                                    <Modal.Header closeButton>
                                    <Modal.Title>feedback</Modal.Title>
                                    </Modal.Header>
                                    <Modal.Body>
                                    <textarea rows = "5" cols = "40" name = "description" placeholder="feedback" onChange={handleChange}>

                                    </textarea>
                                    </Modal.Body>
                                    <Modal.Footer>
                                    <Button variant="secondary" onClick={handleClose}>
                                        Close
                                    </Button>
                                    <Button variant="primary" onClick={handleClose1}>
                                        Submit
                                    </Button>
                                    </Modal.Footer>
                                </Modal>

                            </li>
                            <li className="nav-item text-info">
                                <Link to="/" className="nav-link text-light" ><i class="bi-person-plus-fill"></i>Register</Link>
                            </li>

                        </ul>
                    </div>
                </div>
            </nav>
            <hr />
        </header>
    );
}
export default Header;
