import axios from "axios";
import React from "react";
import { useEffect, useState } from "react";
const ForgotPassword = () => {
    const [userId, setUserId] = useState();
    const [appUser, setAppUser] = useState({});
    const [appUserToAdd, setAppUserToAdd] = useState({});
    const [appUsersList, setAppUsersList] = useState([]);
    //const [name,setName] = useState();
    const [mypic,setMyPic] = useState();
    const dataUrl = `http://localhost:5555/user/na/verify`;
    useEffect(() => {
        console.log(`useEffect`);
        setAppUser({
            id: 101,
            name: "Player-1",
            team: "Team-1",
            mypic: ""
        });
    }, []);

    const handleAddAppUser = (evt) => {
        evt.preventDefault();
        setAppUserToAdd(
            {
                ...appUserToAdd,
                [evt.target.name]: evt.target.value
            });
    }
    const submitAddAppUser = (evt) => {
        evt.preventDefault();
        axios.get(`${dataUrl}/${localStorage.getItem("name")}/${localStorage.getItem("secQuestion")}/${localStorage.getItem("secAnswer")}`)
            .then((response) => {
                alert(`Success`);
                history.push("/");
            })
            .catch(() => {
                alert("Try Again");
            });
    }
    return (<center>
        <div className="container" >
            <p className="display-4 text-dark">Forgot Password</p>

            <div>
                <div className="col-8 border border-light shadow p-3 mb-5 bg-white">
                    <p>Enter the details below to reset your password:</p>
                    <form className="form form-group form-secondary" onSubmit={submitAddAppUser}>
                        <input className="form-control mt-3" type="text" id="userName" name="userName" value={appUserToAdd.userName} onChange={handleAddAppUser} placeholder="Enter name" />
                        <br/>
                        <div>
                            <label className="text-dark display-5">
                                Choose Security Question
                        <select id="secQues" name="secQues" value={appUserToAdd.secQues} onChange={handleAddAppUser}>
                            <option value={appUserToAdd.secQuestion}>What is your mother's maiden name</option>
                            <option value={appUserToAdd.secQuestion}>What is you childhood name</option>
                            <option value={appUserToAdd.secQuestion}>Who was your favourite teacher in elementary school</option>
                        </select>
                        </label>
                        <input className="form-control mt-3" type="text" id="secAnswer" name="secAnswer" value={appUserToAdd.secAnswer} onChange={handleAddAppUser} placeholder="Your Answer" />
                        </div>                        
                        <input className="form-control mt-3 btn btn-success" type="verify" value="Forgot Password" />
                    </form>
                </div>
            </div>
        </div>
        </center>    );
}
export default ForgotPassword;
