import { useState, useEffect} from "react";
import React from "react";
import axios from "axios";
import DemoGetData from "./DemoGetData";
const Viewfav=()=>{
    const [appNews, setappNews] = useState([]);
    const [appNewsCat, setappNewsCat] = useState([]);

    const dataUrl = `http://localhost:2422/news/favs`;
    const deleteUrl=`http://localhost:2422/news/delete`


    useEffect(() => {
        //------------------------------------------------------for loading all data from api
         axios.get(`${dataUrl}/${localStorage.getItem("name")}`)
            .then((response) => {
                console.log(response.data);
                setappNews(response.data);

            })
            .catch((e) => {
                alert("Initialization failed: "+e);
                setappNews([]);
            });


    },[]);
    // const deleteData=(article)=>{
    //     axios.delete(`${deleteUrl}`,{
    //         'Authorization':'Bearer '+localStorage.getItem("head")
    //     })
    //     .then((response)=>{
    //         console.log(article);
    //         alert('Article deleted successfully');
    //     })
    //     .catch(()=>{
    //         alert('Sorry could not be deleted');

    //     });
    // }


    const deleteData=(article)=>{
        if(window.confirm("Are you sure?")){
            fetch(deleteUrl+"/"+article,{
                method: 'DELETE',
                headers: {'Access-Control-Allow-Origin': '*'}

            });     
        }
    }


    return(
        <div >
        <h2 className="display-4 text-dark">Hello {localStorage.getItem("name")}</h2>
        <div className="col-10 border border-light shadow p-3 mb-5 bg-white">
        <div><span className="font-weight-bold"> Your Favourites </span>
{<div><table class="table table-striped">
<tbody>
<tr>
<th scope="col">S.no</th>
<th scope="col">author</th>
<th scope="col">title</th>
<th scope="col">description</th>
<th scope="col">click the url to read more</th>
<th scope="col">urlToImage</th>
</tr>
{appNews.map((item, i) => (
<tr key={i}>
<th scope="row">{i+1}</th>
<td>{item.author}</td>
<td>{item.title}</td>
<td>{item.description}</td>
<td><a href={item.url}>Read more</a></td>
<td> <img src={item.urlToImage} width='100px'/> </td>
<td><button className="form-control mt-3 btn btn-danger" type="submit" onClick={()=>{deleteData(item)}}>delete</button></td>
</tr>
))}
</tbody>
</table> </div>}
</div>
         </div>    
        </div> 
    )
}
export default Viewfav;
