import React, {useState,setState, useEffect} from 'react';
import axios from 'axios';
import './style.css';
import validator from 'validator';

import { useHistory, } from "react-router-dom";
function RegistrationForm() {

    const dataUrl="http://localhost:5555/user/na/signup";

    const [userName, setName] = useState(null);
    const [age, setAge] = useState(null);
    const [email, setEmail] = useState(null);
    const [secQues, setSecQuestion] = useState(null);
    const [gender, setGender] = useState(null);
    const [userId, setId] = useState(null);
    const [secAnswer, setSecAnswer] = useState(null);
    const [password,setPassword] = useState(null);
    const [user,setUser]=useState({});
    const history = useHistory();
    const [errorMessage, setErrorMessage] = useState('');


    const handleInputChange = (e) => {
        setUser(
            {
                ...user,
                [e.target.name]: e.target.value
            });

    }


    const handleSubmit  = (e) => {
        e.preventDefault();
        console.log(user);
        if(errorMessage==='Is Strong Password'){
        axios.post(dataUrl,user)
        .then((response) => {
            alert(`User added successfully ${response.data}`);
            console.log(`User added successfully ${response.data}`);
            history.push("/login");
        })
        .catch((e) => {
            console.log(e);
            alert("User could not be added.");
        });}
        else{
            alert("not a strong password");
        }

    }
    const validate = (value) => {

        if (validator.isStrongPassword(value, {
          minLength: 8, minLowercase: 1,
          minUppercase: 1, minNumbers: 1, minSymbols: 1
        })) {
          setErrorMessage('Is Strong Password');
          setUser(
            {
                ...user,
                "password": value
            });
        } else {
          setErrorMessage('Is Not Strong Password')
        }
      }

    return(
        <div className="form" >
            <div className="form-body">
                <div className="username">
                    <label className="form__label" for="firstName">Name </label>
                    <input className="form__input" name="userName" type="text" value={userName} onChange = {(e) => handleInputChange(e)} id="name" placeholder="Name"/>
                </div>
                {/* <div className="age">
                    <label className="form__label" for="age">Age</label>
                    <input  type="number" name="age" id="age" className="form__input" value={age} onChange = {(e) => handleInputChange(e)} placeholder="Age"/>
                </div> */}
                <div className="id">
                    <label className="form__label" for="id">Id</label>
                    <input  type="number" name="userId" id="userId" className="form__input" value={userId} onChange = {(e) => handleInputChange(e)} placeholder="Id"/>
                </div>
                <input className="form-control mt-3" type="number" id="age" name="age" value={age} onChange = {(e) => handleInputChange(e)} placeholder="Enter age" />
                <div className="Role">
                    <label className="form__label" for="role">Role</label>
                    <input type="radio" className="form__input" id="owner" value="Owner" name="role" onChange={(e) => handleInputChange(e)}/> Owner
                    <input type="radio" className="form__input" id="user" value="User" name="role" onChange={(e) => handleInputChange(e)}/> User
                </div>
                <input className="form-control mt-3" type="text" id="email" name="email" value={email} onChange={(e) => handleInputChange(e)} placeholder="Your Email" />

                <div className="password">
                    <label className="form__label" for="password">Password </label>
                    <input className="form__input" type="password" name="password" id="password" value={password} onChange = {(e) => validate(e.target.value)} placeholder="Password"/>
                    <br></br>
                    <span style={{
                        color: 'red',
                    }}>{errorMessage}</span>
                </div>
                <div className="secQuestion">
                <label className="text-dark display-5">
                                Choose Security Question
                        <select id="secQues" name="secQues" value={secQues} onChange={(e) => handleInputChange(e)}>
                            <option value={secQues}>What is your mother's maiden name</option>
                            <option value={secQues}>What is you childhood name</option>
                            <option value={secQues}>Who was your favourite teacher in elementary school</option>
                        </select>
                        </label>
                </div>

                <div className="secAnswer">
                    <label className="form__label" for="lastName">secAnswer</label>
                    <input  type="text" name="secAnswer" id="secAnswer" value={secAnswer}  className="form__input" onChange = {(e) => handleInputChange(e)} placeholder="SecAnswer"/>
                </div>
                {/* <div className="image">
                <input type="file" name="userPic" id="image" value={img} onChange = {(e) => onImageChange(e)}/>
                </div> */}
                <div class="footer">
                <input onClick={(e)=>handleSubmit(e)} className="form-control btn btn-primary mb-3" type="submit"  value="Register"/>
            </div>

            </div>

        </div>

    )       
}

export default RegistrationForm
