import axios from "axios";
import { useEffect, useState } from "react";

const View = () => {
const [appUsersList, setAppUsersList] = useState([]);
const dataUrl = `http://localhost:4321/users`;
useEffect(() => {
console.log(`useEffect`);
}, []);
const submitGetAllAppUsers = (evt) => {
evt.preventDefault();
axios.get(`${dataUrl}`)
.then((response) => {
console.log(response.data);
setAppUsersList(response.data);
})
.catch(() => {
alert("Users not found.");
setAppUsersList([]);
});
}
return (
<div>
<div className="col-8 border border-light shadow p-3 mb-5 bg-white">
<p>Find All Players</p>
<form className="form form-group form-info" onSubmit={submitGetAllAppUsers}>
<input className="form-control mt-3 btn btn-info" type="submit" value="Find All Players" />
</form>
<div><span className="font-weight-bold"> All Players Data: </span>
{<div><table class="table table-striped">
<tbody>
<tr>
<th scope="col">S.no</th>
<th scope="col">NAME</th>
<th scope="col">ID</th>
<th scope="col">GENDER</th>
<th scope="col">PASSWORD</th>
<th scope="col">SECURITY ANSWER</th>
<th scope="col">IMAGE</th>
</tr>
{appUsersList.map((item, i) => (
<tr key={i}>
<th scope="row">{i+1}</th>
<td>{item.userName}</td>
<td>{item.userId}</td>
<td>{item.gender}</td>
<td>{item.password}</td>
<td>{item.secAnswer}</td>
<td> <img src={item.mypic} width='100px'/> </td>
</tr>
))}
</tbody>
</table> </div>}
</div>
</div>
</div>
);
}
export default View;
