import axios from "axios";
import React from "react";
import { useEffect, useState } from "react";

const SearchRoute = () => {
const [appUsersList, setAppUsersList] = useState([]);
const dataUrl = `http://localhost:1212/bus`;
const localUrl= `http://localhost:1212/bus/route`;
useEffect(() => {
console.log(`useEffect`);
}, []);
const handleInputChange1=(e)=>{
    e.preventDefault();
    setAppUsersList({
        ...bus,
        "routeFrom":e.target.value
    });

}
const handleInputChange2=(e)=>{
    e.preventDefault();
    setAppUsersList({
        ...appUsersList,
        "routeTo":e.target.value
    });
}

const submitGetAllAppUsers = (evt) => {
evt.preventDefault();
axios.get(`${dataUrl}/${appUsersList.routeFrom}/${appUsersList.routeTo}`)
.then((response) => {
console.log(response.data);
setAppUsersList(response.data);
})
.catch(() => {
alert("Data not found.");
setAppUsersList([]);
});
}
return (
<div>
<div className="col-12 border border-light shadow p-3 mb-5 bg-white">
<p>Search All Routes</p>
<form className="form form-group form-info" onSubmit={submitGetAllAppUsers}>
<input className="form__input" name="routeFrom" type="text" value={appUsersList.routeFrom} onChange = {(e) => handleInputChange1(e)} id="routeFrom" placeholder="Location"/>
<input className="form__input" name="routeTo" type="text" value={appUsersList.routeTo} onChange = {(e) => handleInputChange2(e)} id="routeTo" placeholder="Location"/>
<input className="form-control mt-3 btn btn-info" type="submit" value="Search Routes" />
</form>
<div><span className="font-weight-bold"> All Bus Routes Available: </span>
{<div><table class="table table-striped">
<tbody>
<tr>
<th scope="col">S.no</th>
<th scope="col">BusId</th>
<th scope="col">From</th>
<th scope="col">To</th>
<th scope="col">Fare</th>
<th scope="col">Bus Type</th>
<th scope="col">Availability from</th>
<th scope="col">Availability till</th>
</tr>
{appUsersList.map((item, i) => (
<tr key={i}>
<th scope="row">{i+1}</th>
<td>{item.busId}</td>
<td>{item.routeFrom}</td>
<td>{item.routeTo}</td>
<td>{item.fare}</td>
<td>{item.busType}</td>
<td>{item.from}</td>
<td>{item.till}</td>
</tr>
))}
</tbody>
</table> </div>}
</div>
</div>
</div>
);
}
export default SearchRoute;
