import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import './style.css';
import React from "react";
import axios from "axios";


function OwnerHome(){
    const ownerName=localStorage.getItem("userName");
    const [busList,setBusList]=useState([{"busId":"","routeFrom":"","routeTo":"","from":"","till":"12-12-12","type":"non-ac","fare":2000}]);

    const dataUrl = `http://localhost:1212/bus`;
    const deleteUrl=`http://localhost:1212/bus/delete`;

    const getBuses=(e)=>{
        e.preventDefault();
        axios.get(`${dataUrl}`)
                .then((response)=>
                {
                console.log(response.data);
                setBusList(response.data);

                })
                .catch(()=>{
                    alert("no buses found,please add a bus");

                })



    };
    const deleteData=(busId)=>{
        axios.delete(`${deleteUrl}/${localStorage.getItem("name")}/${busId}`)
        .then((busId)=>{
            console.log(busId);
            alert('deleted successfully');
            //fetchData();
            getBuses();
        })
        .catch((err)=>{
            alert('Sorry could not be deleted');
            console.log(err.response)
        });
    }
    return(
        <div className="container">
            <div>
            <Link to="/addbus" className="nav-link" ><button value="Add a new Bus"><i class="bi-plus"></i>Add a new Bus</button></Link>
            </div>
            <div>
            <button value="View your buses" onClick={getBuses}>View your Buses</button>
            </div>          
            <div>
            <div>
      {busList.map((item,i)=>(
      <div className="buscard">
          <div className="busid">
            {item.busId}

          </div>
          <div className="fromto">
            <div className="froma">
                <p>From:{item.routeFrom}</p>
            </div>
            <div className="froma">
                <p>starts at:{item.from}</p>
            </div>


          </div>
          <div className="fromto">
          <div className="froma">
                <p>To:{item.routeTo}</p>
            </div>
            <div className="froma">
                <p>reaches at:{item.till}</p>
            </div>

          </div>
          <div className="type">
              {item.type}

          </div>

          <div className="fare">
              {item.fare}

          </div>


          <div className="delete">
          <button className="form-control mt-3 btn btn-danger" type="submit" onClick={()=>{deleteData(item.busId)}}>delete</button>              

          </div>
      </div>))}
      </div>
            </div>
        </div>
    )

}
export default OwnerHome;
