import { useState } from "react";
import axios from "axios";
import React from "react";

const FavPart=({data})=>{
    const dataUrl=`http://localhost:2422/news`;


    function addToDB(x){

            const news={
                "name":x.name,
                "author":x.author,
                "title":x.title,
                "description":x.description,
                "url":x.url,
                "urlToImage":x.urlToImage,
                "userName":localStorage.getItem("name")

            }
            axios.post(dataUrl, news)
            .then((response) => {

                if(response.data !=""){
                    alert(`Added to Fav`);
                }else{
                    alert("Cannot add to fav");

                }
            })
            .catch((err) => {
                alert("We are currently not available! try after some time: "+err.message);

            });

    }

    return(
        <div className="cardcss">
     <p><span><strong>{data.name}</strong></span></p>
     <p>Title: <span>{data.title}</span></p>
     <p>Description: <span>{data.description}</span></p>
     <img src={data.urlToImage} alt="No image" width="100" height="100"></img><br></br>
     <button onClick={()=>{addToDB(data)}}>Add to Fav</button><br></br>
     <a href={data.url}>Open Site <i class="fa fa-paper-plane"></i></a> &nbsp;

    </div> 
    )
}
export default FavPart;
