import { useState, useEffect} from "react";
import React from "react";
import axios from "axios";
import FavPart from "./FavPart";
const Viewfav=()=>{
    const [appNews, setappNews] = useState([]);
    const [appNewsCat, setappNewsCat] = useState([]);
    const [newsCat, setnewsCat]=useState([]);

    const dataUrl = `http://localhost:2422/news/fav`;


    useEffect(() => {
        //------------------------------------------------------for loading all data from api
         axios.get(`${dataUrl}/${localStorage.getItem("name")}`)
            .then((response) => {
                console.log(response.data);
                setappNews(response.data);

            })
            .catch((e) => {
                alert("Initialization failed: "+e);
                setappNews([]);
            });

            setnewsCat({
                category: 999


            });


    },[]);

    return(
        <div >
        <h1>Hii {localStorage.getItem("name")}</h1>


             {<div style={{ marginLeft: '80px' }} >

                            {appNews.map(item => {

                                return <div class="d-inline-flex p-4 bd-highlight"><FavPart data={item} /></div>

                            })}

                        </div>}
        </div> 
    )
}
export default Viewfav;
