import axios from "axios";
import React from "react";
import { useEffect, useState } from "react";
const Add = () => {
    const [userId, setUserId] = useState();
    const [appUser, setAppUser] = useState({});
    const [appUserToAdd, setAppUserToAdd] = useState({});
    const [appUsersList, setAppUsersList] = useState([]);
    //const [name,setName] = useState();
    const [mypic,setMyPic] = useState();
    const dataUrl = `http://localhost:5555/user/na/signup`;
    useEffect(() => {
        console.log(`useEffect`);
        setAppUser({
            id: 101,
            name: "Player-1",
            team: "Team-1",
            mypic: ""
        });
    }, []);

   const uploadFile = (inputFile) =>
    {
      let rdr=new FileReader();
      rdr.onload=(e)=>{
        let img=new Image();
        img.src=e.target.result;
        img.onload= rs =>{
         setMyPic(e.target.result);
         appUserToAdd.mypic=mypic;
        }
      };
      rdr.readAsDataURL(inputFile.target.files[0]);
    }
    const handleAddAppUser = (evt) => {
        evt.preventDefault();
        setAppUserToAdd(
            {
                ...appUserToAdd,
                [evt.target.name]: evt.target.value
            });
    }
    const submitAddAppUser = (evt) => {
        evt.preventDefault();
        appUserToAdd.mypic=mypic;
        axios.post(dataUrl, appUserToAdd)
            .then((response) => {
                localStorage.setItem("secQuestion",appUserToAdd.secQues);
                localStorage.setItem("secAnswer",appUserToAdd.secAnswer);
                alert(`Player added successfully ${response.data}`);
            })
            .catch(() => {
                alert("Player could not be added.");
            });
    }
    return (<center>
        <div className="container" >
            <p className="display-4 text-dark">Type in your new Password</p>

            <div>
                <div className="col-8 border border-light shadow p-3 mb-5 bg-white">
                    <p>Enter the details below:</p>
                    <form className="form form-group form-secondary" onSubmit={submitAddAppUser}>
                        <input className="form-control mt-3" type="text" id="userName" name="userName" value={appUserToAdd.userName} onChange={handleAddAppUser} placeholder="Enter name" />
                        <input className="form-control mt-3" type="password" id="password" name="password" value={appUserToAdd.password} onChange={handleAddAppUser} placeholder="Enter password" />
                        <input className="form-control mt-3 btn btn-primary" type="submit" value="Add Player" />
                    </form>
                </div>
            </div>
        </div>
        </center>    );
}
export default Add;
