import axios from "axios";
import { useEffect, useState } from "react";

const DemoGetData = () => {
const [appUsersList, setAppUsersList] = useState([]);
const [appUserToAdd, setAppUserToAdd] = useState({});
const dataUrl = `https://newsapi.org/v2/top-headlines?country=in&apiKey=87229b519983412ba54b0c374de74182`;
const localUrl= `http://localhost:2422/news`;
useEffect(() => {
console.log(`useEffect`);
}, []);
const submitGetAllAppUsers = (evt) => {
evt.preventDefault();
axios.get(`${dataUrl}`)
.then((response) => {
console.log(response.data.articles);
setAppUsersList(response.data.articles);
})
.catch(() => {
alert("Data not found.");
setAppUsersList([]);
});
}
const submitAddAppUser = (evt) => {
    evt.preventDefault();
    // appUserToAdd.mypic=mypic;
    axios.post(localUrl, appUserToAdd)
        .then((response) => {
            alert(`Added to favourites ${response.data}`);
        })
        .catch(() => {
            alert("could not be added.");
        });
}
const handleAddAppUser = (evt) => {
    evt.preventDefault();
    setAppUserToAdd(
        {
            ...appUserToAdd,
            [evt.target.name]: evt.target.value
        });
}
function saveData(x){
    alert(x.title);
    alert(x.author);
    alert(x.description);
    alert(x.url);
    alert(x.urlToImage);
}
return (
<div>
<div className="col-8 border border-light shadow p-3 mb-5 bg-white">
<p>click below to get all data</p>
<form className="form form-group form-info" onSubmit={submitGetAllAppUsers }>
<input className="form-control mt-3 btn btn-info" type="submit" value="Find All Data" />
<input className="form-control mt-3 btn btn-info" type="submit" value="Click when ready to add favourite" />
</form>
<div><span className="font-weight-bold"> Data: </span>
{<div><table class="table table-striped">
<tbody>
<tr>
<th scope="col">S.no</th>
<th scope="col">author</th>
<th scope="col">title</th>
<th scope="col">description</th>
<th scope="col">click the url to read more</th>
<th scope="col">urlToImage</th>
</tr>
{appUsersList.map((item, i) => (
<tr key={i}>
<th scope="row">{i+1}</th>
<td>{item.author}</td>
<td>{item.title}</td>
<td>{item.description}</td>
<td><a href={item.url}>Read more</a></td>
<td> <img src={item.urlToImage} width='100px'/> </td>
<td><button className="form-control mt-3 btn btn-info" type="submit" onClick={()=>{handleAddAppUser(item)}} onSubmit={submitAddAppUser}>fav</button></td>
</tr>
))}
</tbody>
</table> </div>}
</div>
</div>
</div>
);
}
export default DemoGetData;
