import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import './style.css';
import React from "react";
import axios from "axios";


function OwnerHome(){
    const ownerName=localStorage.getItem("userName");
    const [busList,setBusList]=useState([{"busId":123,"routeFrom":"Hyderabad","routeTo":"Bangalore","from":"12-12-12","till":"12-12-12","type":"non-ac","fare":2000}]);

    const dataUrl = `http://localhost:1212/bus`;
    const deleteUrl=`http://localhost:1212/bus/delete`;

    const getBuses=(e)=>{
        e.preventDefault();
        axios.get(`${dataUrl}`)
                .then((response)=>
                {
                console.log(response.data);
                setBusList(response.data);

                })
                .catch((err)=>{
                    console.log(err.message);
                    alert("no buses found,please add a bus");

                })



    };
    // const deleteData=(busId)=>{
    //     axios.delete(`${deleteUrl}/${localStorage.getItem("name")}/${busId}`)
    //     .then((busId)=>{
    //         console.log(busId);
    //         alert('deleted successfully');
    //         //fetchData();
    //         getBuses();
    //     })
    //     .catch((err)=>{
    //         alert('Sorry could not be deleted');
    //         console.log(err.response)
    //     });
    // }
    return(
        <div className="container">
            <div className="form" onS>
            <Link to="/addbus" className="nav-link" ><button value="Add a new Bus"><i class="bi-plus"></i>Add a new Bus</button></Link>
            </div>
            <marquee width="90%" direction="up" height="100px" bgcolor="white">Gone are the days when people used to plan for hours to get their bus reservation. Be it long queues in front of ticket booking offices or prolonged waiting time over the telephone, people had to go through a lot of hassles while booking bus tickets. But now the scenario has completely changed in the Indian bus travel industry. With the increasing popularity and adoption of on-demand bus booking apps, now the entire process of making a bus travel reservation has become quick and hassle-free.</marquee>
            <div>
            <button value="View buses" onClick={getBuses}>View all Buses</button>
            </div>          
            <div>
            <div>
      {busList.map((item,i)=>(
      <div className="buscard">
          <div className="busid">
            {item.busId}

          </div>
          <div className="fromto">
            <div className="froma">
                <p>From:{item.routeFrom}</p>
            </div>
            <div className="froma">
                <p>starts at:{item.from.split('T')[0]}</p>
            </div>


          </div>
          <div className="fromto">
          <div className="froma">
                <p>To:{item.routeTo}</p>
            </div>
            <div className="froma">
                <p>reaches at:{item.till.split('T')[0]}</p>
            </div>

          </div>
          <div className="type">
              {item.type}

          </div>

          <div className="fare">
          <i class="fa fa-inr" aria-hidden="true"></i>
              {item.fare}

          </div>


          <div className="delete">
          <button className="form-control mt-3 btn btn-danger" type="submit" >delete</button>              

          </div>
      </div>))}
      </div>
            </div>
        </div>
    )

}
export default OwnerHome;
