import axios from "axios";
import { useEffect, useState } from "react";
const AddPlayers = () => {
    const [userId, setUserId] = useState();
    const [appUser, setAppUser] = useState({});
    const [appUserToAdd, setAppUserToAdd] = useState({});
    const [appUsersList, setAppUsersList] = useState([]);
    //const [name,setName] = useState();
    const [mypic,setMyPic] = useState();
    const dataUrl = `http://localhost:4521/players`;
    useEffect(() => {
        console.log(`useEffect`);
        setAppUser({
            id: 101,
            name: "Player-1",
            team: "Team-1",
            mypic: ""
        });
    }, []);
    const handleChange = (evt) => {
        evt.preventDefault();
        console.log(evt.target.value);
        setUserId(evt.target.value);
    }
    // const add = (evt) => {
    //     evt.preventDefault();
    //     console.log(evt.target.value);
    //     setName("Hello");
    // }
    //const mypic="";
   const uploadFile = (inputFile) =>
    {
      let rdr=new FileReader();
      rdr.onload=(e)=>{
        let img=new Image();
        img.src=e.target.result;
        img.onload= rs =>{
         setMyPic(e.target.result);
         appUserToAdd.mypic=mypic;
        }
      };
      rdr.readAsDataURL(inputFile.target.files[0]);
    }
    const handleAddAppUser = (evt) => {
        evt.preventDefault();
        setAppUserToAdd(
            {
                ...appUserToAdd,
                [evt.target.name]: evt.target.value
            });
    }
    const submitGetAppUserById = (evt) => {
        evt.preventDefault();
        axios.get(`${dataUrl}/${userId}`)
            .then((response) => {
                console.log(response.data);
                setAppUser(response.data);
            })
            .catch(() => {
                alert("Player not found.");
                setUserId();
            });
    }
    const submitAddAppUser = (evt) => {
        evt.preventDefault();
        appUserToAdd.mypic=mypic;
        axios.post(dataUrl, appUserToAdd)
            .then((response) => {
                alert(`Player added successfully ${response.data}`);
            })
            .catch(() => {
                alert("Player could not be added.");
            });
    }
    const submitGetAllAppUsers = (evt) => {
        evt.preventDefault();
        axios.get(`${dataUrl}`)
            .then((response) => {
                console.log(response.data);
                setAppUsersList(response.data);
            })
            .catch(() => {
                alert("Players not found.");
                setAppUsersList([]);
            });
    }
    return (
        <div className="container" >
            <p className="display-4 text-dark">Add Players</p>
            <div>
                <div className="col-6 border border-light shadow p-3 mb-5 bg-white">
                    <p>Enter the details below:</p>
                    <form className="form form-group form-secondary" onSubmit={submitAddAppUser}>
                        <input className="form-control mt-3" type="text" id="playerName" name="playerName" value={appUserToAdd.playerName} onChange={handleAddAppUser} placeholder="Enter name" />
                        <input className="form-control mt-3" type="number" id="playerId" name="playerId" value={appUserToAdd.playerId} onChange={handleAddAppUser} placeholder="Enter id" />
                        <input className="form-control mt-3" type="text" id="team" name="team" value={appUserToAdd.team} onChange={handleAddAppUser} placeholder="Enter team" />
                        <input className="form-control mt-3 btn btn-primary" type="submit" value="Add Player" />
                        <input class="form-control form-control-lg" type="file" id="mypic" name="mypic"  onChange={uploadFile} /><img src={mypic} height="40px"/>
                    </form>
                </div>
            </div>
        </div>
    );
}
export default AddPlayers;
