import axios from "axios";
import React from "react";
import { useEffect, useState } from "react";
const Add = () => {
    const [userId, setUserId] = useState();
    const [appUser, setAppUser] = useState({});
    const [appUserToAdd, setAppUserToAdd] = useState({});
    const [appUsersList, setAppUsersList] = useState([]);
    //const [name,setName] = useState();
    const [mypic,setMyPic] = useState();
    const dataUrl = `http://localhost:5555/user/na/signup`;
    useEffect(() => {
        console.log(`useEffect`);
        setAppUser({
            id: 101,
            name: "Player-1",
            team: "Team-1",
            mypic: ""
        });
    }, []);

   const uploadFile = (inputFile) =>
    {
      let rdr=new FileReader();
      rdr.onload=(e)=>{
        let img=new Image();
        img.src=e.target.result;
        img.onload= rs =>{
         setMyPic(e.target.result);
         appUserToAdd.mypic=mypic;
        }
      };
      rdr.readAsDataURL(inputFile.target.files[0]);
    }
    const handleAddAppUser = (evt) => {
        evt.preventDefault();
        setAppUserToAdd(
            {
                ...appUserToAdd,
                [evt.target.name]: evt.target.value
            });
    }
    const submitAddAppUser = (evt) => {
        evt.preventDefault();
        appUserToAdd.mypic=mypic;
        axios.post(dataUrl, appUserToAdd)
            .then((response) => {
                alert(`Player added successfully ${response.data}`);
            })
            .catch(() => {
                alert("Player could not be added.");
            });
    }
    return (
        <div className="container" >
            <p className="display-4 text-dark">Add Players</p>
            <div>
                <p className="display-5 text-dark">EMAIL : {localStorage.email}</p>
            </div>
            <div>
                <div className="col-8 border border-light shadow p-3 mb-5 bg-white">
                    <p>Enter the details below:</p>
                    <form className="form form-group form-secondary" onSubmit={submitAddAppUser}>
                        <input className="form-control mt-3" type="text" id="userName" name="userName" value={appUserToAdd.userName} onChange={handleAddAppUser} placeholder="Enter name" />
                        <input className="form-control mt-3" type="number" id="userId" name="userId" value={appUserToAdd.userId} onChange={handleAddAppUser} placeholder="Enter id" />
                        <input className="form-control mt-3" type="number" id="age" name="age" value={appUserToAdd.age} onChange={handleAddAppUser} placeholder="Enter age" />
                        <br/>
                        <p className="text-dark display-5">Choose Gender</p>
                        <div className="radio">
                        <label for="male">
                            <input
                            type="radio"
                            id="male"
                            name="gender"
                            value="male"
                            onChange={handleAddAppUser}
                            />
                            male
                        </label>
                        </div>
                        <div className="radio">
                        <label for="female">
                            <input
                            type="radio"
                            id="female"
                            name="gender"
                            value="female"
                            onChange={handleAddAppUser}
                            />
                            female
                        </label>
                        </div>
                        <input className="form-control mt-3" type="text" id="email" name="email" value={appUserToAdd.email} onChange={handleAddAppUser} placeholder="Your Email" />
                        <input className="form-control mt-3" type="password" id="password" name="password" value={appUserToAdd.password} onChange={handleAddAppUser} placeholder="Enter password" />
                        <br/>
                        <div>
                            <label className="text-dark display-5">
                                Choose Security Question
                        <select>
                            <option value={appUserToAdd.secQuestion}>What is your mother's maiden name</option>
                            <option value={appUserToAdd.secQuestion}>What is you childhood name</option>
                            <option value={appUserToAdd.secQuestion}>Who was your favourite teacher in elementary school</option>
                        </select>
                        </label>
                        <input className="form-control mt-3" type="text" id="secAnswer" name="secAnswer" value={appUserToAdd.secAnswer} onChange={handleAddAppUser} placeholder="Your Answer" />
                        </div>                        
                        <input className="form-control mt-3 btn btn-primary" type="submit" value="Add Player" />
                        <input class="form-control form-control-lg" type="file" id="mypic" name="mypic"  onChange={uploadFile} /><img src={mypic} height="40px"/>
                    </form>
                </div>
            </div>
        </div>
    );
}
export default Add;
