import axios from "axios";
import { useEffect, useState } from "react";

const Home = () => {

    const [data, getData] = useState([])
    const URL = 'http://localhost:4521/players';

    useEffect(() => {
        fetchData()
    }, [])


    const fetchData = () => {
        fetch(URL)
            .then((res) =>
                res.json())

            .then((response) => {
                console.log(response);
                getData(response);
            })

    }


    return (
        <div className="container" >
            <p className="display-4 text-dark">Welcome {localStorage.name}</p>
            <div >
                <div className="col-8 border border-light shadow p-3 mb-5 bg-white">
                    <div><span className="font-weight-bold"> All Players Data: </span>
                        {<div><table class="table table-striped">
                            <tbody>
                                <tr>
                                <th scope="col">S.no</th>
                                    <th scope="col">NAME</th>
                                    <th scope="col">ID</th>
                                    <th scope="col">TEAM</th>
                                </tr>
                                {data.map((item, i) => (
                                    <tr key={i}>
                                        <th scope="row">{i+1}</th>
                                        <td>{item.name}</td>
                                        <td>{item.id}</td>
                                        <td>{item.team}</td>

                                    </tr>
                ))}
            </tbody>
                       </table> </div>}
                    </div>
                </div>
            </div>
        </div>
    );

}

export default Home;
